mapper.insert:
INSERT INTO task (
instruction_no,
operator_id,
operator_time,
status,
task_code,
create_time
,task_name
,header_name
,header_idCard
,task_begin_time
,task_end_time
,task_country
,task_description
,group_unit
,visit_purpose
,cost_source
,instruction_time
,has_group_member
,has_faff_plan
,transactor
,faff_plan_no
)
VALUES
(
#{instructionNo},
#{operatorId},
#{operatorTime},
#{status},
#{taskCode},
#{createTime}
,#{taskName}
,#{headerName}
,#{headerIdCard}
,#{taskBeginTime}
,#{taskEndTime}
,#{taskCountry}
,#{taskDescription}
,#{groupUnit}
,#{visitPurpose}
,#{costSource}
,#{instructionTime}
,#{hasGroupMember}
,#{hasFaffPlan}
,#{transactor}
,#{faffPlanNo}
)

mapper.update:
UPDATE task
OPERATOR_ID=#{operatorId}, OPERATOR_TIME=#{operatorTime}, STATUS=#{status}, task_name=#{taskName}, header_name=#{headerName}, header_idCard=#{headerIdCard}, task_begin_time=#{taskBeginTime}, task_end_time=#{taskEndTime}, task_description=#{taskDescription}, task_country=#{taskCountry}, task_code=#{taskCode}, group_unit=#{groupUnit}, visit_purpose=#{visitPurpose}, cost_source=#{costSource}, instruction_time=#{instructionTime}, has_group_member=#{hasGroupMember}, has_faff_plan=#{hasFaffPlan}, transactor=#{transactor}, instruction_no=#{instructionNo}, faff_plan_no=#{faffPlanNo},
WHERE id=#{id}

mapper.select:
		SELECT  t.*, GROUP_CONCAT(a.user_name) as account_name,
		   GROUP_CONCAT(CONCAT(CONCAT_WS(':',CAST(vc.name as char),CAST(tc.expect_stick_day as CHAR)),'å¤©')) as task_country_day 
		FROM
		task t LEFT JOIN task_account ta ON t.id = ta.task_id 
		      LEFT JOIN account a ON ta.account_id = a.id 
		      LEFT JOIN task_country tc ON t.task_code = tc.task_code
		      LEFT JOIN visa_country vc ON tc.task_country = vc.code



				and t.task_name like CONCAT('%',#{taskName},'%')


				and a.user_name like CONCAT('%',#{accountName},'%') 



				and t.task_code like CONCAT('%',#{taskCode},'%')



				and t.header_name like CONCAT('%',#{headerName},'%')



				and t.task_country like CONCAT('%',#{taskCountry},'%')



				and t.status = #{status}



				and t.instruction_no = #{instructionNo}



				and t.task_begin_time >= #{taskBeginTime} 


				and t.task_end_time >= #{taskEndTime} 


            GROUP BY t.task_code

			order by t.${sortField}

				t.${sortOrder}



			limit ${start}, ${maxResults}

SELECT count(distinct(t.task_code))
FROM
task t LEFT JOIN task_account ta ON t.id = ta.task_id LEFT JOIN account a ON ta.account_id = a.id


		and t.task_name like CONCAT('%',#{taskName},'%') 


		and a.user_name like CONCAT('%',#{accountName},'%') 



		and t.task_code like CONCAT('%',#{taskCode},'%')



		and t.header_name like CONCAT('%',#{headerName},'%')



		and t.task_country like CONCAT('%',#{taskCountry},'%')



		and t.status = #{status}



		and t.instruction_no = #{instructionNo}



		and t.task_begin_time >= #{taskBeginTime} 


		and t.task_end_time >= #{taskEndTime}

mapper.insert:
INSERT INTO task_account ( task_id, account_id
) VALUES
(#{taskId},#{accountId})

INSERT INTO task_country (
task_code,
instruction_no,
create_time
,task_country
,expect_stick_day
,comment
)
VALUES
	(#{taskCode} ,#{instructionNo}, #{createTime}
	, #{taskCountry}
	, #{expectStickDay}
	, #{comment}
	)
